-- ----------------------------
-- Table structure for `sys_cms_content`
-- ----------------------------
DROP TABLE IF EXISTS `sys_cms_content`;
CREATE TABLE `sys_cms_content` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `cid` int(11) DEFAULT NULL,
  `title` char(100) DEFAULT NULL,
  `url` varchar(200) DEFAULT NULL,
  `newsdate` char(20) DEFAULT NULL,
  `description` char(255) DEFAULT NULL,
  `headimage` char(200) DEFAULT NULL,
  `imglink` char(100) DEFAULT NULL,
  `catname` char(50) DEFAULT NULL,
  `buildtime` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `openid` char(50) DEFAULT NULL,
  `onclick` int(11) NOT NULL DEFAULT '0',
  `isfirsttitle` tinyint(4) DEFAULT '0',
  `isrecommend` tinyint(4) DEFAULT '0',
  `2weima` char(50) DEFAULT NULL,
  `logourl` char(200) DEFAULT NULL,
  `class_id` int(11) DEFAULT '0',
  `classename` char(20) DEFAULT NULL,
  `titlefenci` char(100) DEFAULT NULL,
  `bk1` char(50) DEFAULT NULL,
  `bk2` char(50) DEFAULT NULL,
  `bk3` char(50) DEFAULT NULL,
  `bk4` char(50) DEFAULT NULL,
  `bk5` char(50) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `class_id` (`class_id`) USING BTREE,
  KEY `cid` (`cid`) USING BTREE,
  KEY `isrecommend` (`isrecommend`) USING BTREE,
  KEY `title` (`title`) USING BTREE,
  KEY `isfirsttitle` (`isfirsttitle`)
) ENGINE=InnoDB AUTO_INCREMENT=1819 DEFAULT CHARSET=utf8;

